
public class FunText2 {

	/**求两个整数的和
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = add(12,13);
		int b = add(124563,68565);
		System.out.println("第一个和为："+a+"  "+"第二个和为："+b);
	}
	public static int add(int a , int b){
		 int sum=0;
		 sum=a+b;
		 return sum;
	}
}
